import { gql, useQuery } from '@apollo/client';
import React from 'react'
import { Link } from 'react-router-dom';
import { Datum, RootObject } from '../model';
import s from './PagesStyles/MainPage.module.scss';

const ARTICLES = gql`
   query GetItems {
      items {
         data {
            id
            attributes {
               name
               description
               rating
            }
         }
      }
   }
`


const MainPage = () => {

   const { loading, error, data } = useQuery(ARTICLES)

   if (loading) return <p>...Loading</p>;
   if (error) return <p>Error!</p>;

   return (
      <div className={s.wrapper}>
         {data.items.data.map((item: Datum) => (
            <div key={item.id} className={s.review}>
               <h2>{item.attributes.name}</h2>
               <div>Рейтинг: {item.attributes.rating}</div>
               <small>console list</small>
               <p>{item.attributes.description.substring(0, 250)}...</p>
               <Link to={`/${item.id}`}>Читать полностью</Link>
            </div>
         ))}
      </div>
   )
}

export default MainPage;
