import { gql } from '@apollo/client';
import React from 'react'
import { Link } from 'react-router-dom';
import { Datum, RootObject } from '../model';
import s from './PagesStyles/MainPage.module.scss';

const REVIEWS = gql`
   query GetItems {
      items {
         data
      }
   }
`
console.log(REVIEWS);


const jsonData: RootObject = require('./../data.json')

const MainPage = () => {

   return (
      <div className={s.wrapper}>
         {jsonData.data.map((review: Datum) => (
            <div key={review.id} className={s.review}>
               <h2>{review.attributes.name}</h2>
               <div>Рейтинг: {review.attributes.rating}</div>
               <small>console list</small>
               <p>{review.attributes.description.substring(0, 250)}...</p>
               <Link to={`/${review.id}`}>Читать полностью</Link>
            </div>
         ))}
      </div>
   )
}

export default MainPage;
